CREATE EXTENSION IF NOT EXISTS plpgsql WITH SCHEMA pg_catalog;

-- ============================================================
-- 1. Tablo Yapıları (Schema)
-- ============================================================

CREATE TABLE public."Address" (
    "Id" uuid NOT NULL,
    "Street" text NOT NULL,
    "City" text NOT NULL,
    "Country" text NOT NULL,
    "ZipCode" text NOT NULL,
    "CustomerId" uuid,
    "CreatedDate" timestamp with time zone NOT NULL,
    "UpdatedDate" timestamp with time zone NOT NULL
);

CREATE TABLE public."AspNetRoleClaims" (
    "Id" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "RoleId" text NOT NULL,
    "ClaimType" text,
    "ClaimValue" text
);

CREATE TABLE public."AspNetRoles" (
    "Id" text NOT NULL,
    "Name" character varying(256),
    "NormalizedName" character varying(256),
    "ConcurrencyStamp" text
);

CREATE TABLE public."AspNetUserClaims" (
    "Id" integer NOT NULL GENERATED BY DEFAULT AS IDENTITY,
    "UserId" text NOT NULL,
    "ClaimType" text,
    "ClaimValue" text
);

CREATE TABLE public."AspNetUserLogins" (
    "LoginProvider" text NOT NULL,
    "ProviderKey" text NOT NULL,
    "ProviderDisplayName" text,
    "UserId" text NOT NULL
);

CREATE TABLE public."AspNetUserRoles" (
    "UserId" text NOT NULL,
    "RoleId" text NOT NULL
);

CREATE TABLE public."AspNetUserTokens" (
    "UserId" text NOT NULL,
    "LoginProvider" text NOT NULL,
    "Name" text NOT NULL,
    "Value" text
);

CREATE TABLE public."AspNetUsers" (
    "Id" text NOT NULL,
    "UserName" character varying(256),
    "NormalizedUserName" character varying(256),
    "Email" character varying(256),
    "NormalizedEmail" character varying(256),
    "EmailConfirmed" boolean NOT NULL,
    "PasswordHash" text,
    "SecurityStamp" text,
    "ConcurrencyStamp" text,
    "PhoneNumber" text,
    "PhoneNumberConfirmed" boolean NOT NULL,
    "TwoFactorEnabled" boolean NOT NULL,
    "LockoutEnd" timestamp with time zone,
    "LockoutEnabled" boolean NOT NULL,
    "AccessFailedCount" integer NOT NULL
);

CREATE TABLE public."Customers" (
    "Id" uuid NOT NULL,
    "FirstName" text NOT NULL,
    "LastName" text NOT NULL,
    "Email" text NOT NULL,
    "IsActive" boolean NOT NULL,
    "CreatedDate" timestamp with time zone NOT NULL,
    "UpdatedDate" timestamp with time zone NOT NULL,
    "AppUserId" uuid DEFAULT '00000000-0000-0000-0000-000000000000'::uuid NOT NULL,
    "PhoneNumber" text DEFAULT ''::text NOT NULL
);

CREATE TABLE public."Files" (
    "Id" uuid NOT NULL,
    "Storage" text NOT NULL,
    "CreatedDate" timestamp with time zone NOT NULL,
    "Discriminator" character varying(21) DEFAULT ''::character varying NOT NULL,
    "FileName" text DEFAULT ''::text NOT NULL,
    "Path" text DEFAULT ''::text NOT NULL,
    "IsPrimary" boolean
);

CREATE TABLE public."ProductBoxes" (
    "Id" uuid NOT NULL,
    "Name" text NOT NULL,
    "Quantity" integer NOT NULL,
    "ProductId" uuid NOT NULL,
    "CreatedDate" timestamp with time zone NOT NULL,
    "UpdatedDate" timestamp with time zone NOT NULL
);

CREATE TABLE public."ProductProductGallery" (
    "ProductGalleriesId" uuid NOT NULL,
    "ProductId" uuid NOT NULL
);

CREATE TABLE public."Products" (
    "Id" uuid NOT NULL,
    "Name" text NOT NULL,
    "Stock" integer NOT NULL,
    "Price" numeric(18,2) NOT NULL,
    "Description" text,
    "Features" text,
    "CreatedDate" timestamp with time zone NOT NULL,
    "UpdatedDate" timestamp with time zone NOT NULL,
    "Category" text DEFAULT ''::text NOT NULL
);

CREATE TABLE public."__EFMigrationsHistory" (
    "MigrationId" character varying(150) NOT NULL,
    "ProductVersion" character varying(32) NOT NULL
);

CREATE SCHEMA IF NOT EXISTS dbo;

CREATE TABLE dbo."CartItems" (
    "Id" uuid NOT NULL,
    "CartId" uuid NOT NULL,
    "ProductId" uuid NOT NULL,
    "Quantity" integer NOT NULL,
    "UnitPrice" numeric(18,2) NOT NULL,
    "CreatedDate" timestamp with time zone NOT NULL,
    "UpdatedDate" timestamp with time zone NOT NULL
);

CREATE TABLE dbo."Carts" (
    "Id" uuid NOT NULL,
    "UserId" uuid,
    "SessionId" character varying(100),
    "ExpiryDate" timestamp with time zone,
    "Status" integer NOT NULL,
    "CreatedDate" timestamp with time zone NOT NULL,
    "UpdatedDate" timestamp with time zone NOT NULL
);

